INSERT INTO tacgia VALUES('31', 'Việt Dương Nhân', '<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Lác đác rơi rơi những lá vàng</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Hồn&nbsp;ai đầy đọng nỗi riêng mang</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Hoàng hôn đang điểm trên đồi vắng</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Sương bạc trải dần&nbsp;khuất mây ngang</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><I></FONT><FONT face=\"times new roman,times,serif\" size=4>&nbsp;</FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Khúc hát tình&nbsp;đưa&nbsp;còn văng vẳng</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Đoản nhạc yêu đương vọng ngút ngàn</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Ôn lại mùa xuân bao thương nhớ</I></FONT></FONT></DIV>\r\n<DIV align=left><FONT color=#9900cc><FONT face=\"times new roman,times,serif\" size=4><I>Nay còn hoài niệm&nbsp;lúc thu sang.</I></FONT></FONT></DIV></I>');
