INSERT INTO truyendai VALUES('1', NULL, 'Bông Hồng Cho Tình Ðầu', '1', '<!--StartFragment -->&nbsp;<SPAN class=s_desc>o với những truyện dài học trò đã và đang được đăng trên trang Thời Áo Trắng thì Thúc nghĩ truyện <B>Bông Hồng Cho Tình Ðầu</B> của tác giả <B>Khánh Như</B> có lẽ là một truyện dài diễn tả chân thật nhất những sinh hoạt ở trường lớp của học trò Việt Nam. Ðọc truyện này sẽ giúp bạn hình dung lại những hình ảnh vui đùa phá phách của mình thuở nào và gợi nhớ thêm về bạn bè và thầy cô ở lớp cũ trường xưa.</SPAN><BR><BR>\r\n<DIV align=center><IMG src=\"http://tretoday.net/truyen/content_images/bhctd.jpg\" border=1></DIV>', '12', '1');
INSERT INTO truyendai VALUES('3', NULL, 'Bản Tình Ca Muôn Thuở', '2', '<P align=center><IMG alt=\"\" hspace=0 src=\"http://tretoday.net/truyen/content_images/629942_bantinhcamuonthuo.jpg\" align=baseline border=0></P>', '113', '1');
INSERT INTO truyendai VALUES('4', NULL, 'Con Gái Thời Nay', '5', '<P align=center><SPAN class=s_desc>Truyện <B>Con Gái Thời Nay</B> sẽ mang lại cho các bạn những hình ảnh hồn nhiên, vui đùa phá phách của mình thuở nào, gợi lại những tình cảm thầm kín, đầy thơ mộng và lãng mạn của lứa tuổi học trò, và cũng gợi nhớ thêm về ghế đá, sân trường, bạn bè và thầy cô của một thời áo trắng và lớp cũ trường xưa.</SPAN><BR></P>', '0', '1');
INSERT INTO truyendai VALUES('5', NULL, 'Áo Trắng Mùa Thu', '6', '<SPAN class=s_desc>Truyện dài \"Áo Trắng Mùa Thu\" của tác giả Khánh Liên do Gia Vân đánh máy. Cảm ơn Gia Vân đã thương mến và ủng hộ TAT</SPAN><BR>', '0', '1');
