INSERT INTO truyendai VALUES('33', '', 'Ði Qua Hoa Cúc', '11', '<SPAN class=s_desc><I>Có một người đi qua hoa cúc<BR><BR>Có hai người đi qua hoa cúc<BR><BR>Bỏ lại sau lưng cả tuổi thơ mình...</I></SPAN><BR>', '0', '1');
