INSERT INTO truyendai VALUES('40', '', 'Hoa Hồng Xứ Khác', '11', '<SPAN class=s_desc>Cũng như nhiều truyện dài khác của nhà văn Nguyễn Nhật Ánh, nội dung của truyện Hoa Hồng Xứ Khác vẫn là tình yêu dễ thương và ngây ngô của tuổi học trò.</SPAN><BR>', '0', '1');
