
DROP TABLE IF EXISTS admin;
CREATE TABLE admin (
   adminid int(10) NOT NULL auto_increment,
   username varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   password varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   email varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   lastlogin int(12),
   ip varchar(20) COLLATE utf8_unicode_ci,
   PRIMARY KEY (adminid)
);




DROP TABLE IF EXISTS adminlogs;
CREATE TABLE adminlogs (
   id int(11) unsigned NOT NULL auto_increment,
   adminid int(11) NOT NULL,
   time int(11) NOT NULL,
   ip varchar(20) COLLATE utf8_unicode_ci NOT NULL,
   action varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   cat varchar(50) COLLATE utf8_unicode_ci,
   pid varchar(250) COLLATE utf8_unicode_ci,
   PRIMARY KEY (id)
);




DROP TABLE IF EXISTS adminsessions;
CREATE TABLE adminsessions (
   s_id varchar(30) NOT NULL,
   adminid int(8) NOT NULL,
   time int(11) NOT NULL,
   ip varchar(20) COLLATE utf8_unicode_ci,
   agent varchar(50) COLLATE utf8_unicode_ci,
   act varchar(50) COLLATE utf8_unicode_ci,
   sub varchar(50) COLLATE utf8_unicode_ci,
   pid varchar(50) COLLATE utf8_unicode_ci
);



DROP TABLE IF EXISTS category;
CREATE TABLE category (
   cid tinyint(4) unsigned NOT NULL auto_increment,
   cname varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   pact varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   corder tinyint(4) DEFAULT '127' NOT NULL,
   PRIMARY KEY (cid)
);




DROP TABLE IF EXISTS config;
CREATE TABLE config (
   user varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   password varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   email varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   count int(8) NOT NULL
);




DROP TABLE IF EXISTS contents;
CREATE TABLE contents (
   id int(11) unsigned NOT NULL auto_increment,
   cat varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   ten varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   tacgia varchar(250) COLLATE utf8_unicode_ci,
   noidung text NOT NULL,
   xem int(11) NOT NULL,
   ngaydang int(11) NOT NULL,
   nguoidang int(11) DEFAULT '1' NOT NULL,
   active tinyint(1) DEFAULT '1' NOT NULL,
   PRIMARY KEY (id)
);




DROP TABLE IF EXISTS luubut;
CREATE TABLE luubut (
   id int(11) unsigned NOT NULL auto_increment,
   ten varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   email varchar(150) NOT NULL,
   diachi varchar(250) COLLATE utf8_unicode_ci,
   yahoo varchar(50) COLLATE utf8_unicode_ci,
   noidung text NOT NULL,
   trinhduyet varchar(250) COLLATE utf8_unicode_ci,
   ip varchar(16),
   active tinyint(1) DEFAULT '1' NOT NULL,
   PRIMARY KEY (id)
);




DROP TABLE IF EXISTS sessions;
CREATE TABLE sessions (
   s_id varchar(32) NOT NULL,
   ip varchar(16) DEFAULT '0.0.0.0' NOT NULL,
   agent varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   userid int(8) NOT NULL,
   location varchar(250) COLLATE utf8_unicode_ci,
   time varchar(32) NOT NULL
);




DROP TABLE IF EXISTS setting;
CREATE TABLE setting (
   s_key varchar(50) COLLATE utf8_unicode_ci NOT NULL,
   s_value text,
   s_type int(2) NOT NULL,
   s_des text
);




DROP TABLE IF EXISTS tacgia;
CREATE TABLE tacgia (
   tg_id int(11) unsigned NOT NULL auto_increment,
   tg_ten varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   tg_gioithieu text,
   PRIMARY KEY (tg_id)
);




DROP TABLE IF EXISTS truyendai;
CREATE TABLE truyendai (
   tid int(11) unsigned NOT NULL auto_increment,
   cat varchar(250) COLLATE utf8_unicode_ci,
   ten varchar(250) COLLATE utf8_unicode_ci NOT NULL,
   tacgia int(8) NOT NULL,
   gioithieu text,
   landoc int(11) NOT NULL,
   status tinyint(1) NOT NULL,
   PRIMARY KEY (tid)
);



